import React from "react";
import axios from "axios";
import sha256 from "sha256";
import { storage } from "./firebase";

class Admin extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      admin: null,
      title: "",
      description: "",
      price: "",
      products: [],
      image: [],
      url: "",
      submitable: false,
      loading: ""
    };

    this.handleDelete = this.handleDelete.bind(this);
    this.handleChange1 = this.handleChange1.bind(this);
    this.handleChange2 = this.handleChange2.bind(this);
    this.handleChange3 = this.handleChange3.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChangeImage = this.handleChangeImage.bind(this);
  }

  handleChange1(e) {
    this.setState({
      title: e.target.value
    });
  }

  handleChange2(e) {
    this.setState({
      description: e.target.value
    });
  }

  handleChange3(e) {
    this.setState({
      price: e.target.value
    });
  }

  handleChangeImage(e) {
    var self = this;
    console.log(self);
    if (e.target.files[0]) {
      for (var i = 0; i < e.target.files.length; i++) {
        const image = e.target.files[i];
        const uploadTask = storage.ref(`images/${image.name}`).put(image);
        uploadTask.on(
          "state_changed",
          snapshot => {
            //progress function
          },
          error => {
            //error function
            console.log(error);
          },
          () => {
            var self2 = self;
            // complete function
            storage
              .ref("images")
              .child(image.name)
              .getDownloadURL()
              .then(url => self2.SubmitApi(url));
          }
        );
      }
    }
  }

  submiatble() {}

  handleDelete(id) {
    axios.delete(`http://localhost:8000/api/product/delete/${id}`);
  }

  handleSubmit(e) {
    e.preventDefault();
    console.log(this.state.image);
    const product = {
      title: this.state.title,
      description: this.state.description,
      price: this.state.price,
      image: this.state.image
    };

    let uri = "http://localhost:8000/api/product/add";

    axios.post(uri, product).then(response => {
      console.log(response);
    });
  }

  SubmitApi(url) {
    this.setState({ image: [...this.state.image, url] });
  }

  componentDidMount() {
    axios
      .get(`http://127.0.0.1:8000/api/loginCheck`, {
        mode: "no-cors",
        headers: {
          Authorization:
            "Bearer " + sha256(JSON.parse(localStorage.getItem("token"))),
          "Access-Control-Allow-Origin": "*",
          "Content-Type": "application/json"
        }
      })

      .then(res => {
        this.setState({ admin: res.data });
      });

    axios
      .get(`http://127.0.0.1:8000/api/products`, {
        mode: "no-cors",
        headers: {
          "Access-Control-Allow-Origin": "*",
          "Content-Type": "application/json"
        }
      })

      .then(res => {
        const data = res.data;
        this.setState({ products: data.produits });
      });
  }

  handleLogout() {
    localStorage.removeItem("token");
  }

  render() {
    const { admin } = this.state;
    const { products } = this.state;

    if (admin === null) {
      return <div />;
    } else if (admin === true) {
      return (
        <div>
          <nav className="navbar navbar-dark bg-dark">
            <a className="navbar-brand" href="/">
              <b>
                <i className="fa fa-shopping-cart" /> E-COMMERCE
              </b>
            </a>
            <div className="navbar-brand" href="/">
              <a className="nav-item" href="/">
                <i className="fa fa-home" /> Accueil
              </a>
              <a className="nav-item" href="/">
                <i className="fa fa-home" /> Mon ps
              </a>
              <a
                className="nav-item"
                href="/"
                onClick={() => this.handleLogout()}
              >
                <i className="fa fa-sign-out" /> Déconnexion
              </a>
            </div>
          </nav>
          <div>
            <form id="addform" method="POST" onSubmit={this.handleSubmit}>
              <div className="card form mt-5">
                <h1 className=" title-card mt-3">
                  <i className="fa fa-plus" /> Ajouter une annonce
                </h1>
                <div className="card-body">
                  <div className="form-group">
                    <label>Titre:</label>
                    <input
                      type="text"
                      className="form-control"
                      id="title"
                      onChange={this.handleChange1}
                      placeholder="Votre titre..."
                    />
                  </div>
                  <div className="form-group">
                    <label>Description:</label>
                    <input
                      type="text"
                      className="form-control"
                      id="description"
                      onChange={this.handleChange2}
                      placeholder="Votre description..."
                    />
                  </div>
                  <div className="form-group">
                    <label>Prix:</label>
                    <input
                      type="integer"
                      className="form-control"
                      id="price"
                      onChange={this.handleChange3}
                      placeholder="Votre prix..."
                    />
                  </div>
                  <label>Ajouter photos: </label>
                  <div class="input-group mb-4">
                    <div class="custom-file">
                      <input
                        onChange={this.handleChangeImage}
                        type="file"
                        multiple
                        class="custom-file-input"
                        id="inputGroupFile01"
                        aria-describedby="inputGroupFileAddon01"
                      />
                      <label class="custom-file-label" for="inputGroupFile01">
                        Choisir photos
                      </label>
                    </div>
                  </div>

                  <button type="submit" className="btn btn-primary large-btn">
                    <i className="fa fa-plus" /> Ajouter
                  </button>
                </div>
              </div>
            </form>
          </div>
          <div className="container-table mt-5 mb-5">
            <table className="table">
              <thead>
                <tr>
                  <th scope="col">id</th>
                  <th scope="col">Titre</th>
                  <th scope="col">Description</th>
                  <th scope="col">Prix</th>
                  <th scope="col">Supprimer</th>
                  <th scope="col">Modifier</th>
                </tr>
              </thead>
              <tbody>
                {products.map(product => (
                  <tr key={product.id}>
                    <th scope="row">{product.id}</th>
                    <td>{product.title}</td>
                    <td>{product.description}</td>
                    <td>{product.price} €</td>
                    <td>
                      <a
                        className="cursor-click"
                        onClick={() => this.handleDelete(product.id)}
                      >
                        <i className="fa fa-trash fa-2x" />
                      </a>
                    </td>
                    <td>
                      <a href={"edit/product/" + product.id}>
                        <i className="fa fa-edit fa-2x" />
                      </a>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      );
    } else {
      return (
        <div className="container mt-4">
          <div className="alert alert-danger center-alert" role="alert">
            <i className="fa fa-warning" /> Il faut être
            <strong> administrateur</strong> pour accéder a cette page !
            <a href="/"> Retourner à l'accueil...</a>
            <button
              type="button"
              className="close"
              data-dismiss="alert"
              aria-label="Close"
            >
              <span aria-hidden="true">&times;</span>
            </button>
          </div>
        </div>
      );
    }
  }
}
export default Admin;
