import React from 'react';
import axios from 'axios';

import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      products: [],
    };
  }

  componentDidMount() {
    axios.get(`http://127.0.0.1:8001/api/products`, { mode: 'no-cors', headers: { 'Access-Control-Allow-Origin' : '*', 'Content-Type' : 'application/json' } })

      .then(res => {
        const data = res.data;
        console.log(data);
        this.setState({ products : data });
      })
  }

  render() {
    const { products } = this.state;

    return (
      <ul> 
        {products.map(product => (
          <div>
            Titre: {product.title} 
            Description: {product.description}
            Prix: {product.price}
            Ajoute le: ()
          </div>
        ))}
      </ul>
  );
  }
}

export default App;
