import React from 'react';
import axios from 'axios';

import './App.css';
import './bootstrap.min.css';


class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      products: [],
    };
  }

  componentDidMount() {
    axios.get(`http://127.0.0.1:8001/api/products`, { mode: 'no-cors', headers: { 'Access-Control-Allow-Origin' : '*', 'Content-Type' : 'application/json' } })

      .then(res => {
        const data = res.data;
        console.log(data);
        this.setState({ products : data });
      })
  }

  render() {
    const { products } = this.state;

    return (
      <ul>
        <div className="card" style="width: 18rem;"> 
          {products.map(product => (
            <div>
              Titre: <b>{product.title}</b>
              Description: <b>{product.description}</b>
              Prix: <b>{product.price} â‚¬</b>
              Ajoute le: <b>{product.created_at}</b>
            </div>
          ))}
        </div>
      </ul>
  );
  }
}

export default App;
