'use strict';

const ipRegex = require('ip-regex');

const tlds = require('tlds');

module.exports = opts => {
  opts = Object.assign({
    strict: true
  }, opts);
  const protocol = "(?:(?:[a-z]+:)?//)".concat(opts.strict ? '' : '?');
  const auth = '(?:\\S+(?::\\S*)?@)?';
  const ip = ipRegex.v4().source;
  const host = '(?:(?:[a-z\\u00a1-\\uffff0-9]-*)*[a-z\\u00a1-\\uffff0-9]+)';
  const domain = '(?:\\.(?:[a-z\\u00a1-\\uffff0-9]-*)*[a-z\\u00a1-\\uffff0-9]+)*';
  const tld = "(?:\\.".concat(opts.strict ? '(?:[a-z\\u00a1-\\uffff]{2,})' : "(?:".concat(tlds.sort((a, b) => b.length - a.length).join('|'), ")"), ")\\.?");
  const port = '(?::\\d{2,5})?';
  const path = '(?:[/?#][^\\s"]*)?';
  const regex = "(?:".concat(protocol, "|www\\.)").concat(auth, "(?:localhost|").concat(ip, "|").concat(host).concat(domain).concat(tld, ")").concat(port).concat(path);
  return opts.exact ? new RegExp("(?:^".concat(regex, "$)"), 'i') : new RegExp(regex, 'ig');
};
