import React from "react";
import axios from "axios";
import sha256 from "sha256";

class Admin extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      admin: null,
      title: "",
      description: "",
      price: "", 
      products: [],
    };

    this.handleChange1 = this.handleChange1.bind(this);
    this.handleChange2 = this.handleChange2.bind(this);
    this.handleChange3 = this.handleChange3.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange1(e) {
    this.setState({
      title: e.target.value
    });
  }

  handleChange2(e) {
    this.setState({
      description: e.target.value
    });
  }

  handleChange3(e) {
    this.setState({
      price: e.target.value
    });
  }

  handleSubmit(e) {
    e.preventDefault();
    const product = {
      title: this.state.title,
      description: this.state.description,
      price: this.state.price
    };

    let uri = "http://localhost:8000/api/product/add";

    axios.post(uri, product).then(response => {
      console.log(response);
    });
  }

  componentDidMount() {
    axios
      .get(`http://127.0.0.1:8000/api/loginCheck`, {
        mode: "no-cors",
        headers: {
          Authorization:
            "Bearer " + sha256(JSON.parse(localStorage.getItem("token"))),
          "Access-Control-Allow-Origin": "*",
          "Content-Type": "application/json"
        }
      })

      .then(res => {
        this.setState({ admin: res.data });
      });

      axios
      .get(`http://127.0.0.1:8000/api/products`, {
        mode: "no-cors",
        headers: {
          "Access-Control-Allow-Origin": "*",
          "Content-Type": "application/json"
        }
      })

      .then(res => {
        const data = res.data;
        this.setState({ products: data });
      });
  }

  render() {
    const { admin } = this.state;
    const { products } = this.state;


    if (admin === null) {
      return <div />;
    } else if (admin === true) {
      return (
        <div>
          <nav className="navbar navbar-dark bg-dark">
            <a
              className="navbar-brand"
              href="https://reactjs.org/docs/lists-and-keys.html#keys"
            >
              <b>
                <i className="fa fa-shopping-cart" /> E-COMMERCE
              </b>
            </a>
            <div
              className="navbar-brand"
              href="https://reactjs.org/docs/lists-and-keys.html#keys"
            >
              <a className="nav-item" href="/">
                <i className="fa fa-home" /> Accueil
              </a>
            </div>
          </nav>
          <div>
            <form id="addform" method="POST" onSubmit={this.handleSubmit}>
              <div className="card mt-5">
                <h1 className=" title-card mt-3">
                  <i className="fa fa-plus" /> Ajouter une annonce
                </h1>
                <div className="card-body">
                  <div className="form-group">
                    <label>Titre:</label>
                    <input
                      type="text"
                      className="form-control"
                      id="title"
                      onChange={this.handleChange1}
                      placeholder="Votre titre..."
                    />
                  </div>
                  <div className="form-group">
                    <label>Description:</label>
                    <input
                      type="text"
                      className="form-control"
                      id="description"
                      onChange={this.handleChange2}
                      placeholder="Votre description..."
                    />
                  </div>
                  <div className="form-group">
                    <label>Prix:</label>
                    <input
                      type="integer"
                      className="form-control"
                      id="price"
                      onChange={this.handleChange3}
                      placeholder="Votre prix..."
                    />
                  </div>
                  <button type="submit" className="btn btn-primary large-btn">
                    <i className="fa fa-plus" /> Ajouter
                  </button>
                </div>
              </div>
            </form>
          </div>
          <table class="container mt-5 table">
            <thead>
              <tr>
                <th scope="col">id</th>
                <th scope="col">Titre</th>
                <th scope="col">Description</th>
                <th scope="col">Prix</th>
                <th scope="col">Supprimer</th>
                <th scope="col">Modifier</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <th scope="row">1</th>
                <td>Mark</td>
                <td>Otto</td>
                <td>@mdo</td>
                <td><i class="fa fa-trash fa-2x"></i></td>
                <td><i class="fa fa-edit fa-2x"></i></td>
              </tr>
            </tbody>
          </table>
          {products.map(product => (
            <div key={product.id} className="card">
              <img
                className="card-img-top"
                src="data:image/svg+xml;charset=UTF-8,%3Csvg%20width%3D%22286%22%20height%3D%22180%22%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20viewBox%3D%220%200%20286%20180%22%20preserveAspectRatio%3D%22none%22%3E%3Cdefs%3E%3Cstyle%20type%3D%22text%2Fcss%22%3E%23holder_16b4bc3c694%20text%20%7B%20fill%3Argba(255%2C255%2C255%2C.75)%3Bfont-weight%3Anormal%3Bfont-family%3AHelvetica%2C%20monospace%3Bfont-size%3A14pt%20%7D%20%3C%2Fstyle%3E%3C%2Fdefs%3E%3Cg%20id%3D%22holder_16b4bc3c694%22%3E%3Crect%20width%3D%22286%22%20height%3D%22180%22%20fill%3D%22%23777%22%3E%3C%2Frect%3E%3Cg%3E%3Ctext%20x%3D%22106.3984375%22%20y%3D%2296.3%22%3E286x180%3C%2Ftext%3E%3C%2Fg%3E%3C%2Fg%3E%3C%2Fsvg%3E"
                alt="Card"
              />

              </div>
          ))}
        </div>
      );
    } else {
      return (
        <div className="container mt-4">
          <div className="alert alert-danger" role="alert">
            <i className="fa fa-warning" /> Il faut être{" "}
            <strong>administrateur</strong> pour accéder a cette page !{" "}
            <a href="/">Retourner à l'accueil...</a>
            <button
              type="button"
              className="close"
              data-dismiss="alert"
              aria-label="Close"
            >
              <span aria-hidden="true">&times;</span>
            </button>
          </div>
        </div>
      );
    }
  }
}
export default Admin;
