import React from "react";
import axios from "axios";

class Product extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      product: []
    };
  }

  componentDidMount() {
    const uri = window.location.href;
    const id = uri.split("/");
    axios
      .get(`http://127.0.0.1:8001/api/product/` + id[4], {
        mode: "no-cors",
        headers: {
          "Access-Control-Allow-Origin": "*",
          "Content-Type": "application/json"
        }
      })

      .then(res => {
        const data = res.data;
        this.setState({ product: data });
        console.log(res.data);
      });
  }

  render() {
    const { product } = this.state;

    return (
      <div>
        <nav className="navbar navbar-dark bg-dark">
          <a className="navbar-brand" href="/">
            <b>
              <i className="fa fa-shopping-cart" /> E-COMMERCE
            </b>
          </a>
          <div className="navbar-brand" href="/">
            <a className="nav-item" href="/">
              <i className="fa fa-home" /> Accueil
            </a>
            <a className="nav-item" href="/login">
              <i className="fa fa-sign-in" /> Connexion
            </a>
            <a className="nav-item" href="/register">
              <i className="fa fa-user-plus" /> Inscription
            </a>
          </div>
        </nav>
        <div>
          <div className="card mt-4">
            <img
              className="card-img-top"
              src="data:image/svg+xml;charset=UTF-8,%3Csvg%20width%3D%22286%22%20height%3D%22180%22%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20viewBox%3D%220%200%20286%20180%22%20preserveAspectRatio%3D%22none%22%3E%3Cdefs%3E%3Cstyle%20type%3D%22text%2Fcss%22%3E%23holder_16b4bc3c694%20text%20%7B%20fill%3Argba(255%2C255%2C255%2C.75)%3Bfont-weight%3Anormal%3Bfont-family%3AHelvetica%2C%20monospace%3Bfont-size%3A14pt%20%7D%20%3C%2Fstyle%3E%3C%2Fdefs%3E%3Cg%20id%3D%22holder_16b4bc3c694%22%3E%3Crect%20width%3D%22286%22%20height%3D%22180%22%20fill%3D%22%23777%22%3E%3C%2Frect%3E%3Cg%3E%3Ctext%20x%3D%22106.3984375%22%20y%3D%2296.3%22%3E286x180%3C%2Ftext%3E%3C%2Fg%3E%3C%2Fg%3E%3C%2Fsvg%3E"
              alt="Card"
            />
            <div className="card-body">
              <h5 className="card-title">
                <b>{product.title}</b>
              </h5>
              <p className="card-text">
                <b>{product.description}</b>
              </p>
              <p className="card-text">
                Prix: <b>{product.price} €</b>
              </p>
              <p className="card-text">
                Ajouté le: <b>{product.created_at}</b>
              </p>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
export default Product;
