import React from "react";
import axios from "axios";


class Login extends React.Component {
  componentDidMount() {
    const form = document.getElementById("formpost");

    const formData = new FormData(form);
    formData.append("title", "The frustration with post request");

    form.onsubmit = () =>
      axios.post("http://localhost:8001/api/login", formData).then(response => response.data);

      axios
      .get(`http://127.0.0.1:8001/api/login`, {
        mode: "no-cors",
        headers: {
          "Access-Control-Allow-Origin": "*",
          "Content-Type": "application/json"
        }
      })

      .then(res => {
        const data = res.data;
console.log(data)
      });
  }


  render() {
    return (
      <div>
        <nav className="navbar navbar-dark bg-dark">
          <a className="navbar-brand" href="/">
            <b>
              <i className="fa fa-shopping-cart" /> E-COMMERCE
            </b>
          </a>
          <div className="navbar-brand" href="/">
            <a className="nav-item" href="/">
              <i className="fa fa-home" /> Accueil
            </a>
            <a className="nav-item" href="/login">
              <i className="fa fa-sign-in" /> Connexion
            </a>
            <a className="nav-item" href="/register">
              <i className="fa fa-user-plus" /> Inscription
            </a>
          </div>
        </nav>
        <div>
          <div className="card login">
            <form
              id="formpost"
              method="POST"
              action="http://localhost:8001/api/login"
              acceptCharset="UTF-8"
              onSubmit={this.handleSubmit}
            >
              {" "}
              <h1>
                <i className="fa fa-sign-in" /> Se connecter
              </h1>
              <hr />
              <label>
                <h5 className="font-login">Nom d'utilisateur :</h5>
                <input
                  name="username"
                  className="input-login"
                  type="text"
                  onChange={this.handleChange}
                />
                <h5 className="font-login">Mot de passe :</h5>
                <input name="password" type="password" onChange={this.handleChange} />
              </label>
              <div>
                <button type="submit" className="btn btn-outline-secondary">
                  <i className="fa fa-sign-in" /> Se connecter
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    );
  }
}
export default Login;
