import React from "react";
import axios from "axios";
import sha256 from "sha256";

class Admin extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      admin: null,
      title: "",
      description: "",
      price: ""
    };

    this.handleChange1 = this.handleChange1.bind(this);
    this.handleChange2 = this.handleChange2.bind(this);
    this.handleChange3 = this.handleChange3.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange1(e) {
    this.setState({
      title: e.target.value
    });
  }

  handleChange2(e) {
    this.setState({
      description: e.target.value
    });
  }

  handleChange3(e) {
    this.setState({
      price: e.target.value
    });
  }

  handleSubmit(e) {
    e.preventDefault();
    const product = {
      title: this.state.title,
      description: this.state.description,
      price: this.state.price
    };

    let uri = "http://localhost:8000/api/product/add";

    axios.post(uri, product).then(response => {
      console.log(response);
    });
  }

  componentDidMount() {
    axios
      .get(`http://127.0.0.1:8000/api/loginCheck`, {
        mode: "no-cors",
        headers: {
          Authorization:
            "Bearer " + sha256(JSON.parse(localStorage.getItem("token"))),
          "Access-Control-Allow-Origin": "*",
          "Content-Type": "application/json"
        }
      })

      .then(res => {
        this.setState({ admin: res.data });
      });
  }

  render() {
    const { admin } = this.state;

    if (admin === null) {
      return <div />;
    } else if (admin === true) {
      return (
        <div>
          <nav className="navbar navbar-dark bg-dark">
            <a
              className="navbar-brand"
              href="https://reactjs.org/docs/lists-and-keys.html#keys"
            >
              <b>
                <i className="fa fa-shopping-cart" /> E-COMMERCE
              </b>
            </a>
            <div
              className="navbar-brand"
              href="https://reactjs.org/docs/lists-and-keys.html#keys"
            >
              <a className="nav-item" href="/">
                <i className="fa fa-home" /> Accueil
              </a>
            </div>
          </nav>
          <div>
            <form id="addform" method="POST" onSubmit={this.handleSubmit}>
              <div className="card mt-5">
                <h1 className=" title-card mt-3">
                  <i className="fa fa-plus" /> Ajouter une annonce
                </h1>
                <div className="card-body">
                  <div className="form-group">
                    <label>Titre:</label>
                    <input
                      type="text"
                      className="form-control"
                      id="title"
                      onChange={this.handleChange1}
                      placeholder="Votre titre..."
                    />
                  </div>
                  <div className="form-group">
                    <label>Description:</label>
                    <input
                      type="text"
                      className="form-control"
                      id="description"
                      onChange={this.handleChange2}
                      placeholder="Votre description..."
                    />
                  </div>
                  <div className="form-group">
                    <label>Prix:</label>
                    <input
                      type="integer"
                      className="form-control"
                      id="price"
                      onChange={this.handleChange3}
                      placeholder="Votre prix..."
                    />
                  </div>
                  <button type="submit" className="btn btn-primary large-btn">
                    <i className="fa fa-plus" /> Ajouter
                  </button>
                </div>
              </div>
            </form>
          </div>
          <table class="container mt-5 table">
            <thead>
              <tr>
                <th scope="col">id</th>
                <th scope="col">Titre</th>
                <th scope="col">Description</th>
                <th scope="col">Prix</th>
                <th scope="col">Prix</th>

              </tr>
            </thead>
            <tbody>
              <tr>
                <th scope="row">1</th>
                <td>Mark</td>
                <td>Otto</td>
                <td>@mdo</td>
                <td>test</td>
              </tr>
            </tbody>
          </table>
        </div>
      );
    } else {
      return (
        <div className="container mt-4">
          <div className="alert alert-danger" role="alert">
            <i className="fa fa-warning" /> Il faut être{" "}
            <strong>administrateur</strong> pour accéder a cette page !{" "}
            <a href="/">Retourner à l'accueil...</a>
            <button
              type="button"
              className="close"
              data-dismiss="alert"
              aria-label="Close"
            >
              <span aria-hidden="true">&times;</span>
            </button>
          </div>
        </div>
      );
    }
  }
}
export default Admin;
