import React from "react";
import axios from "axios";
import sha256 from 'sha256';

class ProductEdit extends React.Component {
  constructor(props) {
    super(props);

    this.state = { admin: null, title: "", description: "", price: "", availability: "0", product: [] };

    this.handleChange1 = this.handleChange1.bind(this);
    this.handleChange2 = this.handleChange2.bind(this);
    this.handleChange3 = this.handleChange3.bind(this);
    this.handleChange4 = this.handleChange4.bind(this);
  }

  componentDidMount() {
    const uri = window.location.href;
    const id = uri.split("/");
    axios
      .get(`http://127.0.0.1:8000/api/product/` + id[5], {
        mode: "no-cors",
        headers: {
          "Access-Control-Allow-Origin": "*",
          "Content-Type": "application/json"
        }
      })

      .then(res => {
        const data = res.data;
        this.setState({
          product: data,
          description: data.description,
          price: data.price,
          title: data.title
        });
      });

      axios
      .get(`http://127.0.0.1:8000/api/loginCheck`, {
          mode: "no-cors",
          headers: {
              Authorization:
                  "Bearer " + sha256(JSON.parse(localStorage.getItem("token"))),
              "Access-Control-Allow-Origin": "*",
              "Content-Type": "application/json"
          }
      })

      .then(res => {
          this.setState({admin: res.data});
      });
  }

  handleChange1(e) {
    this.setState({
      title: e.target.value
    });
  }

  handleChange2(e) {
    this.setState({
      description: e.target.value
    });
  }

  handleChange3(e) {
    this.setState({
      price: e.target.value
    });
  }

  handleChange4(e) {
    this.setState({
      availability: e.target.checked
    });
  }

  handleSubmit(e) {
    e.preventDefault();
    const product = {
      title: this.state.title,
      description: this.state.description,
      price: this.state.price,
      availability: this.state.availability
    };

    const test = window.location.href;
    const id_prod = test.split("/");

    let uri = `http://localhost:8000/api/edit/product/` + id_prod[5];

    axios.post(uri, product).then(response => {
      console.log(response);
    });
  }

  render() {
    const {admin} = this.state;
    if (admin === null) {
      return <div/>;
  } else if (admin === true) {
    return (
      <div>
        <nav className="navbar navbar-dark bg-dark">
          <a className="navbar-brand" href="/">
            <b>
              <i className="fa fa-shopping-cart" /> E-COMMERCE
            </b>
          </a>
          <div className="navbar-brand" href="/">
            <a className="nav-item" href="/">
              <i className="fa fa-home" /> Accueil
            </a>
            <a className="nav-item" href="/login">
              <i className="fa fa-sign-in" /> Connexion
            </a>
            <a className="nav-item" href="/register">
              <i className="fa fa-user-plus" /> Inscription
            </a>
          </div>
        </nav>
        <div>
          <div className="card form mt-4">
            <img
              className="card-img-top"
              src="data:image/svg+xml;charset=UTF-8,%3Csvg%20width%3D%22286%22%20height%3D%22180%22%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20viewBox%3D%220%200%20286%20180%22%20preserveAspectRatio%3D%22none%22%3E%3Cdefs%3E%3Cstyle%20type%3D%22text%2Fcss%22%3E%23holder_16b4bc3c694%20text%20%7B%20fill%3Argba(255%2C255%2C255%2C.75)%3Bfont-weight%3Anormal%3Bfont-family%3AHelvetica%2C%20monospace%3Bfont-size%3A14pt%20%7D%20%3C%2Fstyle%3E%3C%2Fdefs%3E%3Cg%20id%3D%22holder_16b4bc3c694%22%3E%3Crect%20width%3D%22286%22%20height%3D%22180%22%20fill%3D%22%23777%22%3E%3C%2Frect%3E%3Cg%3E%3Ctext%20x%3D%22106.3984375%22%20y%3D%2296.3%22%3E286x180%3C%2Ftext%3E%3C%2Fg%3E%3C%2Fg%3E%3C%2Fsvg%3E"
              alt="Card"
            />
            <form
              id="addform"
              method="POST"
              onSubmit={this.handleSubmit.bind(this)}
            >
              <div className="card-body">
                <h5 className="card-title">
                  <b>Titre:</b>
                </h5>
                <input
                  type="text"
                  className="input-edit mb-3"
                  value={this.state.title}
                  onChange={this.handleChange1}
                />
                <h5 className="card-title">
                  <b>Description:</b>
                </h5>
                <textarea
                  rows="4"
                  cols="50"
                  type="text"
                  className="input-edit mb-3"
                  value={this.state.description}
                  onChange={this.handleChange2}
                />
                <h5 className="card-title">
                  <b>Prix:</b>
                </h5>
                <input
                  type="text"
                  className="input-edit"
                  value={this.state.price}
                  onChange={this.handleChange3}
                />
                <div class="form-check">
                  <input
                    type="checkbox"
                    class="mb-3 mt-4 form-check-input"
                    onChange={this.handleChange4}
                  />
                  <label class="mb-3 mt-4 form-check-label checkboxStatus" for="exampleCheck1">
                    Coch√© si produit en <strong>rupture de stock</strong>.
                  </label>
                </div>
                <button type="submit" class="mt-3 btn btn-primary input-edit">
                  <i class="icon fa fa-edit" /> Modifier
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    )};

  }
}
export default ProductEdit;
