import React from "react";
import axios from "axios";
import sha256 from "sha256";

import "./App.css";
import "./bootstrap.min.css";

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      products: [],
      count: [],
      categories: [],
      countAvailability: [],
      countAvailabilityT: [],
      countUser: []
    };
  }

  setProductCart(e) {
    e.preventDefault();
    const id = e.target.value;

    axios
      .get(`http://127.0.0.1:8000/api/product/` + id, {
        mode: "no-cors",
        headers: {
          "Access-Control-Allow-Origin": "*",
          "Content-Type": "application/json"
        }
      })

      .then(res => {
        const data = res.data;

        var showProducts = [];

        var show = {
          id: data.id,
          title: data.title,
          description: data.description,
          price: data.price,
          picture: data.pictures
        };

        showProducts.push(show);
        showProducts = showProducts.concat(
          JSON.parse(localStorage.getItem("Products") || "[]")
        );
        console.log(showProducts);

        localStorage.setItem("Products", JSON.stringify(showProducts));
      });
  }

  componentDidMount() {
    if (JSON.parse(localStorage.getItem("token")) == null) {
      axios
        .get(`http://127.0.0.1:8000/api/loginCheck`, {
          mode: "no-cors",
          headers: {
            "Access-Control-Allow-Origin": "*",
            "Content-Type": "application/json"
          }
        })

        .then(res => {
          this.setState({ admin: res.data });
        });
    } else {
      axios
        .get(`http://127.0.0.1:8000/api/loginCheck`, {
          mode: "no-cors",
          headers: {
            Authorization:
              "Bearer " + sha256(JSON.parse(localStorage.getItem("token"))),
            "Access-Control-Allow-Origin": "*",
            "Content-Type": "application/json"
          }
        })

        .then(res => {
          this.setState({ admin: res.data });
        });
    }

    axios
      .get(`http://127.0.0.1:8000/api/products`, {
        mode: "no-cors",
        headers: {
          "Access-Control-Allow-Origin": "*",
          "Content-Type": "application/json"
        }
      })

      .then(res => {
        const data = res.data;
        this.setState({ products: data.produits });
        this.setState({ count: data.count });
        this.setState({ countAvailability: data.countAvailability });
        this.setState({ countAvailabilityT: data.countAvailabilityT });
        this.setState({ countUser: data.countUser });
      });

    axios
      .get(`http://127.0.0.1:8000/api/categories`, {
        mode: "no-cors",
        headers: {
          "Access-Control-Allow-Origin": "*",
          "Content-Type": "application/json"
        }
      })

      .then(res => {
        const data = res.data;
        this.setState({ categories: data });
      });
  }

  handleLogout() {
    localStorage.removeItem("token");
    localStorage.removeItem("Products");
  }

  render() {

    return (
        <div>

        </div>
    );
  }
}

export default Flash;
